from azure.data.tables import TableClient

class TableConnector:
    def __init__(self,config):
        self.config = config

        # Connect to Azure table
        connection_str = "DefaultEndpointsProtocol=https;AccountName=" + self.config.get(
            'storage.account_name') + ";AccountKey=" + \
                         self.config.get('storage.account_key') + ";EndpointSuffix=core.windows.net"

        self._table_service = TableClient.from_connection_string(connection_str,
                                                           table_name=self.config.get('storage.table_name'))



    def search(self, partition_key, row_key,columns):
        filter = "PartitionKey eq '{}' and RowKey eq '{}'".format(partition_key,row_key)

        query_result = self._table_service.query_entities(query_filter=filter, select=columns)


        result = {}
        for query in query_result:
            for col_names in columns:
                result[col_names] = query[col_names]

        return  result
