from neo4j import GraphDatabase
import os
import time

class GraphAdapter:
    def __init__(self, config, logger):
        self._config = config
        self._logger = logger
        self._connector = GraphDatabase.driver(self._config['neo4j.bolt_url'], auth=(self._config['neo4j.user_name'], self._config['neo4j.password']),encrypted=False)

    def execute_query(self, query):
        connector = None
        try:
            self._logger.info(f"Query execution initiated.")
            start = time.time()
            with self._connector.session(database=self._config['neo4j.database']) as session:
                res = session.write_transaction(self._execute_txn, query)
            end = time.time()
            self._logger.info(f"Query execution completed. Execution Time - {end - start} seconds.")
            return res
        except Exception as e:
            self._logger.error(f"Query Execution Failed - {e}")

        
    def _execute_txn(self, tx, query):
        res = tx.run(query)
        values = []
        for record in res:
            values.append(dict(record))
        return values


    def execute_update_query(self, query, **kwargs):
        connector = None
        try:
            with self._connector.session(database=self._config['neo4j.database']) as session:
                res = session.write_transaction(self._execute_arg_txn, query, kwargs)
            return res
        except Exception as e:
            raise e

    def execute_read_query(self, query, **kwargs):
        connector = None
        try:
            with self._connector.session(database=self._config['neo4j.database']) as session:
                res = session.read_transaction(self._execute_arg_txn, query, kwargs)
            return res
        except Exception as e:
            raise e
        
    def _execute_arg_txn(self, tx, query, data):
        res = tx.run(query, data)
        values = []
        for record in res:
            values.append(dict(record))
        return values
    
        
    def close(self):
        try: 
            if self._connector:
                self._connector.close()
        except Exception as e:
            self._logger.error(f"Exception while closing the connection - {e}")
